mapper.update:
UPDATE kehu
kehu_py=#{kehu_py,jdbcType=VARCHAR}, kehu_name=#{kehu_name,jdbcType=VARCHAR}, lxr=#{lxr,jdbcType=VARCHAR}, tel=#{tel,jdbcType=VARCHAR}, sj=#{sj,jdbcType=VARCHAR}, leibie=#{leibie,jdbcType=VARCHAR}, city=#{city,jdbcType=VARCHAR}, web=#{web,jdbcType=VARCHAR}, mail=#{mail,jdbcType=VARCHAR}, cz=#{cz,jdbcType=VARCHAR}, bank=#{bank,jdbcType=VARCHAR}, province=#{province,jdbcType=VARCHAR}, adr=#{adr,jdbcType=VARCHAR}, comment=#{comment,jdbcType=VARCHAR},
WHERE kehu_no=#{kehu_no,jdbcType=VARCHAR}

mapper.select:

	SELECT * 
	FROM kehu 
	WHERE 1=1 AND kehu_del='0' 

 AND kehu_no<>#{kehu_no}  
 AND kehu_name=#{kehu_name}
