mapper.update:
UPDATE peij
peij_py=#{peij_py,jdbcType=VARCHAR}, peij_name=#{peij_name,jdbcType=VARCHAR}, peij_lb=#{peij_lb,jdbcType=VARCHAR}, code=#{code,jdbcType=VARCHAR}, brand=#{brand,jdbcType=VARCHAR}, unit=#{unit,jdbcType=VARCHAR}, in_price=#{in_price,jdbcType=DOUBLE}, out_price1=#{out_price1,jdbcType=DOUBLE}, jinyong=#{jinyong,jdbcType=VARCHAR}, peij_del=#{peij_del,jdbcType=VARCHAR},
WHERE peij_no=#{peij_no,jdbcType=VARCHAR}

mapper.select:

	SELECT * 
	FROM peij 
	WHERE 1=1 AND peij_del='0' 

 AND peij_no<>#{peij_no}  
 AND peij_name=#{peij_name} 
 AND peij_lb=#{peij_lb}
